// src/components/Add.jsx

import { useState } from "react";
import axios from "axios";
import style from '../style/Add.module.css';
import InputMonto from "./InputMonto";

// 1. Recibimos el 'token' como prop
function Add({ onMovementAdded, token }) {
  const [fecha] = useState(new Date().toISOString().split("T")[0]);
  const [ingresoMonto, setIngresoMonto] = useState('');
  const [egresoMonto, setEgresoMonto] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // 2. Creamos una variable para saber si el formulario debe estar deshabilitado
  const isDisabled = !token;

  const handleSubmit = async (e, tipo, monto) => {
    e.preventDefault();
    // La validación del token ya está implícita porque el form está deshabilitado,
    // pero mantenemos la validación interna por seguridad.
    if (!token) {
      return setError("Debes iniciar sesión para añadir un movimiento.");
    }

    // ... el resto de la función handleSubmit se queda exactamente igual ...
    setError('');
    setLoading(true);

    const form = e.target;
    const montoNumerico = parseFloat(monto);

    if (isNaN(montoNumerico) || montoNumerico <= 0) {
      setLoading(false);
      return setError("Por favor, ingresa un monto válido.");
    }

    const data = { tipo, monto: montoNumerico, categoria: form.categoria.value, fecha: form.fecha.value, detalle: form.detalle.value };

    try {
      await axios.post("http://localhost:3000/api/add", data, {
        headers: { Authorization: `Bearer ${token}` }
      });
      form.reset();
      if (tipo === "ingreso") setIngresoMonto('');
      else setEgresoMonto('');
      if (onMovementAdded) onMovementAdded();
    } catch (err) {
      setError(err.response?.data?.error || "Error al guardar el movimiento");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={style.container}>
      {loading && <div className={style.spinner}></div>}
      <p className={style.title}>Añadir ingreso / Añadir egreso</p>
      <p className={style.subTitle}>Fecha</p>

      {/* 3. Envolvemos los formularios en un fieldset y un div para el overlay */}
      <div className={style.formContainer}>
        {/* Si está deshabilitado, mostramos una capa por encima con un mensaje */}
        {isDisabled && (
          <div className={style.disabledOverlay}>
            <p>Inicia sesión para añadir movimientos</p>
          </div>
        )}

        {/* El fieldset deshabilita todos los inputs y buttons que contiene */}
        <fieldset disabled={isDisabled} className={style.fieldset}>
          <div className={style.containerAllForm}>
            <form className={style.formone} onSubmit={(e) => handleSubmit(e, "ingreso", ingresoMonto)}>
              <InputMonto className={style.input} value={ingresoMonto} onChange={setIngresoMonto} />
              <div className={style.containerBtn}>
                <input name="categoria" className={style.btn} placeholder="Categoría" required />
                <input name="fecha" className={style.date} type="date" defaultValue={fecha} />
              </div>
              <div className={style.containerDetalle}>
                <input name="detalle" className={style.detalle} placeholder="Detalle" />
                <button className={style.buttonSend} type="submit" disabled={loading}>Añadir ingreso</button>
              </div>
            </form>
            <form className={style.formtwo} onSubmit={(e) => handleSubmit(e, "egreso", egresoMonto)}>
              <InputMonto className={style.input} value={egresoMonto} onChange={setEgresoMonto} />
              <div className={style.containerBtn}>
                <input name="categoria" className={style.btn} placeholder="Categoría" required />
                <input name="fecha" className={style.date} type="date" defaultValue={fecha} />
              </div>
              <div className={style.containerDetalle}>
                <input name="detalle" className={style.detalle} placeholder="Detalle" />
                <button className={style.buttonSend} type="submit" disabled={loading}>Añadir egreso</button>
              </div>
            </form>
          </div>
        </fieldset>
      </div>

      {error && <p style={{ color: 'red', textAlign: 'center', marginTop: '1rem' }}>{error}</p>}
    </div>
  );
}

export default Add;
