// src/components/Add.jsx
import { useState } from "react";
import axios from "axios";
import style from '../style/Add.module.css';
import InputMonto from "./InputMonto";

function Add({ onMovementAdded, token }) {
  const [fecha] = useState(new Date().toISOString().split("T")[0]);
  const [ingresoMonto, setIngresoMonto] = useState('');
  const [egresoMonto, setEgresoMonto] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const isDisabled = !token;

  const handleSubmit = async (e, tipo, monto) => {
    e.preventDefault();
    if (!token) return setError("Debes iniciar sesión para añadir un movimiento.");
    setError('');
    setLoading(true);
    // ...resto de tu función handleSubmit
  };

  return (
    <div className={style.container}>
      <p className={style.title}>Añadir ingreso / Añadir egreso</p>
      <p className={style.subTitle}>Fecha</p>
      <div className={style.formContainer} style={{position: 'relative'}}>
        {isDisabled && (
          <div style={{position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(30,30,30,0.8)', color: 'white', display: 'flex', justifyContent: 'center', alignItems: 'center', borderRadius: '8px', zIndex: 10, textAlign: 'center'}}>
            <p>Inicia sesión para añadir movimientos</p>
          </div>
        )}
        <fieldset disabled={isDisabled} style={{border: 'none', padding: 0}}>
          <div className={style.containerAllForm}>
            {/* Formulario de Ingreso */}
            <form className={style.formone} onSubmit={(e) => handleSubmit(e, "ingreso", ingresoMonto)}>
              {/* ... contenido del form ... */}
            </form>
            {/* Formulario de Egreso */}
            <form className={style.formtwo} onSubmit={(e) => handleSubmit(e, "egreso", egresoMonto)}>
              {/* ... contenido del form ... */}
            </form>
          </div>
        </fieldset>
      </div>
      {error && <p style={{ color: 'red', textAlign: 'center', marginTop: '1rem' }}>{error}</p>}
    </div>
  );
}

export default Add;
