// src/AppRoutes.jsx (CORREGIDO Y COMPLETO)

import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import MainLayout from './MainLayout';
import Dashboard from './Dashboard';
import Tareas from './Tareas';

// 1. Recibimos las props de edición (`taskToEdit`, `setTaskToEdit`, `onTaskUpdate`)
function AppRoutes({ token, onAuthSuccess, onLoginClick, onCloseModal, activeView, onMovementAdded, onLogout, movimientos, refreshKey, taskToEdit, setTaskToEdit, onTaskUpdate }) {
  return (
    <Routes>
      <Route
        path="/"
        element={
          // 2. Pasamos las props de edición a MainLayout
          <MainLayout 
            token={token} 
            onLogout={onLogout} 
            movimientos={movimientos}
            taskToEdit={taskToEdit}
            onTaskAdded={onTaskUpdate} // Pasamos la función de actualización
          />
        }
      >
        <Route
          index
          element={
            <Dashboard
              token={token}
              onAuthSuccess={onAuthSuccess}
              onLoginClick={onLoginClick}
              onCloseModal={onCloseModal}
              activeView={activeView}
              onMovementAdded={onMovementAdded}
              movimientos={movimientos}
              refreshKey={refreshKey}
            />
          }
        />
        <Route
          path="notas"
          element={
            token ? (
              <Tareas 
                token={token} 
                refreshKey={refreshKey}
                // 3. Pasamos la función para INICIAR la edición a Tareas
                onEditClick={setTaskToEdit} 
              />
            ) : (
              <Navigate to="/" />
            )
          }
        />
      </Route>
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default AppRoutes;
