// Results.js (Versión Final - Sin cambiar tu render)

import { useEffect, useState } from 'react';
import axios from 'axios';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import style from '../style/Results.module.css';

// Recibe todas las props necesarias desde App.js
function Results({ onLoginClick, activeView, onCloseModal, token, onAuthSuccess }) {
  const [movimientos, setMovimientos] = useState([]);

  // ESTE useEffect ES LA CLAVE DE TODO
  useEffect(() => {
    const fetchMovimientos = async () => {
      // Si no hay token, no hacemos nada.
      if (!token) {
        setMovimientos([]); // Aseguramos que los movimientos estén vacíos si no hay token
        return;
      }

      try {
        // La petición ahora envía el token de autorización
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        // Si el token es inválido, el backend dará un error. Limpiamos los movimientos.
        setMovimientos([]);
      }
    };

    fetchMovimientos();
  }, [token]); // <-- Se ejecuta cada vez que el 'token' cambia

  const formatCurrency = (number) => { /* ... */ };

  return (
    <div className={style.container}>
      <div>
        <h2>Movimientos</h2>

        {/* ==================================================================== */}
        {/* BLOQUE DE AUTENTICACIÓN: No cambia su lógica. */}
        {/* Solo se muestra si el array de 'movimientos' está vacío. */}
        {/* ==================================================================== */}
        {movimientos.length === 0 && !activeView && (
          <SignupPage 
            onSwitchToLogin={onLoginClick} 
            onAuthSuccess={onAuthSuccess} // <-- Pasa la función de éxito
          />
        )}

        {movimientos.length === 0 && activeView === 'login' && (
          <LoginPage 
            onClose={onCloseModal} 
            onAuthSuccess={onAuthSuccess} // <-- Pasa la función de éxito
          />
        )}

        {/* ==================================================================== */}
        {/* BLOQUE DE MOVIMIENTOS: No cambia su lógica. */}
        {/* Automáticamente se mostrará cuando el array 'movimientos' se llene. */}
        {/* ==================================================================== */}
        <div className={style.containerInfoAll}>
          {movimientos.map((mov) => (
            <div className={`${style.containerInfo} ${mov.tipo === 'ingreso' ? style.bordeIngreso : style.bordeEgreso}`} key={mov._id}>
              <div className={style.info}>
                <p className={style.category}>{mov.categoria}</p>
                <p className={style.detalle}>{mov.detalle}</p>
              </div>
              <p className={style.monto}>{formatCurrency(mov.monto)}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Results;
