import axios from "axios";
import { useState, useEffect } from "react";
import style from '../style/Tarea.module.css'

// ✅ La corrección está aquí, en las llaves {}
function Tareas({ token, refreshKey }) {

  const [tasks, setTasks]  = useState([]); // Cambié el nombre a 'tasks' para más claridad

  useEffect(() => {
    const fetchTasks = async () => {
      if (!token) {
        setTasks([]); 
        return;
      }
      try {
        const res = await axios.get("http://localhost:3000/api/task", {
          headers: { Authorization: `Bearer ${token}` }
        });

        // --- AÑADE ESTE CONSOLE.LOG AQUÍ ---
        console.log("✅ DATOS RECIBIDOS DEL BACKEND:", res.data);

        setTasks(res.data);
      } catch (err) {
        console.error("Error al obtener las tareas:", err);
        setTasks([]);
      }
    };
    fetchTasks();
  }, [token, refreshKey]);

  // Pequeños ajustes para mostrar las tareas
  return (
    <div className={style.container} >
      <h1>Mis Tareas</h1>
      <div>
        {tasks.length === 0 ? (
          <p>
            Aún no tienes tareas. ¡Añade una para comenzar! NOOOOOO
          </p>
        ) : (
          tasks.map((task) => (

            // Es importante añadir una 'key' única a cada elemento de un map
            <div key={task._id} >
              <div >
                <p >{task.meta}</p>
                <p >Urgencia: {task.urgencia}</p>
              </div>
              <div >
                {/* Formateamos la fecha para que sea más legible */}
                <p>Fecha:  {new Date(task.fecha).toLocaleDateString()}</p>
                <p>Horario: {task.horario}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Tareas;
