// App.js

import { useState } from "react"; // <--- 1. IMPORTAMOS useState
import { Routes, Route, Navigate } from "react-router-dom";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
import LoginPage from "./LoginPage";
import SignupPage from "./SignupPage";
import style from "../style/App.module.css";

function PrivateRoute({ children }) {
  // Tu lógica de autenticación iría aquí
  return children;
}

// ====================================================================
// 2. CREAMOS UN COMPONENTE DE LAYOUT DENTRO DE APP.JS
// Este componente organiza tu vista principal y distribuye las props.
// ====================================================================
function PrivateLayout({ onNavClick, showResultsContent }) {
  return (
    <div className={style.container}>
      {/* 4. Le pasamos la función que viene de App al Nav */}
      <Nav /> 

      <div className={style.content}>
        <LeftsSite />
        <div className={style.contentSide}>
          {/* 5. Le pasamos el estado que viene de App al Results */}
          <Results  onBotonClick={onNavClick}  mostrarContenidoExtra={showResultsContent} /> 
          <Add />
        </div>
      </div>
    </div>
  );
}

// ====================================================================
// TU COMPONENTE APP AHORA ES MÁS SENCILLO
// ====================================================================
function App() {
  // 1. El estado y la función para manejarlo viven aquí, en el padre.
  const [showContent, setShowContent] = useState(false);

  const handleNavClick = () => {
    // Puedes agregar lógica más compleja aquí si quieres
    setShowContent(true); 
  };

  return (
    <Routes>
      {/* Rutas públicas */}
      <Route path="/login" element={<LoginPage />} />
      <Route path="/register" element={<SignupPage />} />

      {/* Rutas privadas */}
      <Route
        path="/"
        element={
          <PrivateRoute>
            {/* 6. Usamos nuestro layout y le pasamos el estado y la función */}
            <PrivateLayout 
              onNavClick={handleNavClick} 
              showResultsContent={showContent} 
            />
          </PrivateRoute>
        }
      />

      {/* Redirección a Home si ruta no existe */}
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
