import "react-datepicker/dist/react-datepicker.css";
import style from "../style/LeftsideNotas.module.css";
import { useState, useEffect } from "react";
import axios from "axios";
import DatePicker from "react-datepicker";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:3000";

const initialFormData = {
  meta: "",
  fecha: new Date(),
  horario: "12:00",
  urgencia: "importante",
  color: "color1",
  esRecurrente: false,
  diasRepeticion: [],
};

function LeftSideNotas({ onUpdate = () => {}, taskToEdit }) {
  const [isOpen, setIsOpen] = useState(true);
  const [formData, setFormData] = useState(initialFormData);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);

  const isDesktop = !window.matchMedia("(max-width: 1000px)").matches;

  /* ===============================
     RESPONSIVE OPEN / CLOSE
  =============================== */
  useEffect(() => {
    const mediaQuery = window.matchMedia("(max-width: 1000px)");

    const handleResize = (e) => {
      setIsOpen(!e.matches); // desktop open | mobile closed
    };

    handleResize(mediaQuery);
    mediaQuery.addEventListener("change", handleResize);

    return () => mediaQuery.removeEventListener("change", handleResize);
  }, []);

  /* ===============================
     EDIT MODE
  =============================== */
  useEffect(() => {
    if (taskToEdit) {
      const fechaUTC = new Date(taskToEdit.fecha);
      fechaUTC.setMinutes(fechaUTC.getMinutes() + fechaUTC.getTimezoneOffset());

      setFormData({
        ...taskToEdit,
        fecha: fechaUTC,
        diasRepeticion: taskToEdit.diasRepeticion || [],
      });

      setIsOpen(true);
    } else {
      setFormData(initialFormData);
    }
  }, [taskToEdit]);

  const toggleContainer = () => setIsOpen((prev) => !prev);

  /* ===============================
     HANDLERS
  =============================== */
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleDateChange = (date) => {
    setFormData((prev) => ({ ...prev, fecha: date }));
  };

  const handleColorSelect = (color) => {
    setFormData((prev) => ({ ...prev, color }));
  };

  const toggleDia = (dia) => {
    setFormData((prev) => ({
      ...prev,
      diasRepeticion: prev.diasRepeticion.includes(dia)
        ? prev.diasRepeticion.filter((d) => d !== dia)
        : [...prev.diasRepeticion, dia],
    }));
  };

  /* ===============================
     SUBMIT
  =============================== */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setLoading(true);

    if (!formData.meta) {
      setError("Por favor, escribe el nombre de la meta.");
      setLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) throw new Error("No est√°s autenticado.");

      const fechaLocal = new Date(formData.fecha);
      fechaLocal.setMinutes(
        fechaLocal.getMinutes() - fechaLocal.getTimezoneOffset()
      );

      const dataToSend = {
        ...formData,
        fecha: fechaLocal.toISOString().slice(0, 10),
      };

      if (taskToEdit) {
        await axios.put(
          `${API_URL}/api/task/${taskToEdit._id}`,
          dataToSend,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setSuccess("¬°Tarea actualizada con √©xito!");
      } else {
        await axios.post(`${API_URL}/api/task`, dataToSend, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSuccess("¬°H√°bito a√±adido con √©xito!");
      }

      onUpdate();

      setTimeout(() => {
        if (!taskToEdit) setFormData(initialFormData);
        if (!isDesktop) setIsOpen(false); // üî• SOLO MOBILE
        setSuccess("");
      }, 900);
    } catch (err) {
      setError(err.response?.data?.message || "Hubo un error al guardar.");
    } finally {
      setLoading(false);
    }
  };

  const isEditing = !!taskToEdit;

  /* ===============================
     RENDER
  =============================== */
  return (
    <div className={style.container}>
      <div className={style.containerOpen}>
        <div className={style.containerOpenClose}>
          <p className={style.close} onClick={toggleContainer}>
            {isDesktop ? "Crear un h√°bito +" : isOpen ? "‚úï" : "Crear un h√°bito +"}
          </p>
        </div>
      </div>

      {isOpen && (
        <>
          <p className={style.titleKeep}>
            {isEditing ? "Editar H√°bito" : "Crear un h√°bito"}
          </p>

          {error && <p style={{ color: "red" }}>{error}</p>}
          {success && <p style={{ color: "green" }}>{success}</p>}

          <form className={style.form} onSubmit={handleSubmit}>
            <input
              name="meta"
              placeholder="Escriba su meta"
              value={formData.meta}
              onChange={handleChange}
            />

            <DatePicker
              selected={formData.fecha}
              onChange={handleDateChange}
              dateFormat="dd-MM-yyyy"
              className={style.datePicker}
            />

            <input
              type="time"
              name="horario"
              value={formData.horario}
              onChange={handleChange}
            />

            <select
              name="urgencia"
              value={formData.urgencia}
              onChange={handleChange}
              className={style.select}
            >
              <option value="importante">Importante</option>
              <option value="urgente">Urgente</option>
              <option value="no importante">No Importante</option>
              <option value="obligaciones">Obligaciones</option>
            </select>

            <div className={style.containerColors}>
              {["color1", "color2", "color3", "color4"].map((c, i) => (
                <div
                  key={c}
                  className={`${style[`circle${["One","Two","Three","Four"][i]}`]} ${
                    formData.color === c ? style.selected : ""
                  }`}
                  onClick={() => handleColorSelect(c)}
                />
              ))}
            </div>

            <div className={style.containerKeep}>
              <p className={style.keep}>Repetir h√°bito</p>
              <input
                type="checkbox"
                name="esRecurrente"
                checked={formData.esRecurrente}
                onChange={handleChange}
              />
            </div>

            {formData.esRecurrente && (
              <div className={style.diasContainer}>
                {["D", "L", "M", "MI", "J", "V", "S"].map((dia) => (
                  <label key={dia} className={style.diaItem}>
                    <input
                      type="checkbox"
                      checked={formData.diasRepeticion.includes(dia)}
                      onChange={() => toggleDia(dia)}
                    />
                    <span>{dia}</span>
                  </label>
                ))}
              </div>
            )}

            <button className={style.btn} disabled={loading}>
              {loading
                ? "Guardando..."
                : isEditing
                ? "Guardar cambios"
                : "A√±adir h√°bito"}
            </button>
          </form>
        </>
      )}
    </div>
  );
}

export default LeftSideNotas;
