import axios from "axios";
import { useState, useEffect } from "react";
// ...otros imports
import DatePicker from 'react-datepicker';

// 1. PRIMERO el CSS de la librer√≠a
import 'react-datepicker/dist/react-datepicker.css';
import style from '../style/Tarea.module.css';

// Funci√≥n auxiliar para obtener la fecha de hoy en formato YYYY-MM-DD
const getTodayString = () => {
  const today = new Date();
  // Ajustamos por la zona horaria para evitar problemas de un d√≠a antes/despu√©s
  today.setMinutes(today.getMinutes() - today.getTimezoneOffset());
  return today.toISOString().slice(0, 10);
};

function Tareas({ token, refreshKey }) {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 1. Nuevo estado para guardar la fecha seleccionada. Inicia con el d√≠a de hoy.
  const [selectedDate, setSelectedDate] = useState(getTodayString());

  useEffect(() => {
    const fetchTasks = async () => {
      if (!token) {
        setTasks([]);
        return;
      }
      setLoading(true);
      setError('');
      try {
        // 2. A√±adimos la fecha seleccionada como un par√°metro en la URL
        const res = await axios.get(`http://localhost:3000/api/task?fecha=${selectedDate}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log(`‚úÖ DATOS RECIBIDOS DEL BACKEND PARA LA FECHA ${selectedDate}:`, res.data);
        setTasks(res.data);
      } catch (err) {
        console.error("Error al obtener las tareas:", err);
        setError("No se pudieron cargar las tareas para esta fecha.");
        setTasks([]);
      } finally {
        setLoading(false);
      }
    };
    fetchTasks();
    // 3. Hacemos que el useEffect se ejecute de nuevo si la fecha seleccionada cambia
  }, [token, refreshKey, selectedDate]);

  // 4. Funci√≥n para manejar el cambio en el input de fecha
  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
  };
  const renderContent = () => {
    if (loading) {
      return <p>Cargando tareas...</p>;
    }

    if (error) {
      return <p style={{ color: 'red' }}>{error}</p>;
    }

    if (tasks.length === 0) {
      return <p>A√∫n no tienes tareas. ¬°A√±ade una para comenzar!</p>;
    }

    // --- 1. A√ëADE ESTA NUEVA FUNCI√ìN ---
    const handleToggleComplete = async (taskId, currentStatus) => {
      try {
        // Hacemos la petici√≥n PUT a la nueva ruta, enviando el estado opuesto
        const response = await axios.put(`http://localhost:3000/api/task/${taskId}`,
          { completada: !currentStatus }, // El nuevo estado
          { headers: { Authorization: `Bearer ${token}` } }
        );

        // 2. Actualizamos el estado localmente para una respuesta instant√°nea
        setTasks(prevTasks =>
          prevTasks.map(task =>
            task._id === taskId ? response.data : task
          )
        );

      } catch (err) {
        console.error("Error al actualizar la tarea:", err);

      }
    };

     // --- 1. A√ëADE LA NUEVA FUNCI√ìN PARA BORRAR ---
    const handleDeleteTask = async (taskId) => {
      // Opcional: Pedir confirmaci√≥n al usuario
      if (!window.confirm("¬øEst√°s seguro de que quieres eliminar esta tarea?")) {
        return;
      }

      try {
        // Hacemos la petici√≥n DELETE a la nueva ruta del backend
        await axios.delete(`http://localhost:3000/api/task/${taskId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        // 2. Actualizamos el estado para eliminar la tarea de la vista al instante
        setTasks(prevTasks => prevTasks.filter(task => task._id !== taskId));

      } catch (err) {
        console.error("Error al eliminar la tarea:", err);
        // Podr√≠as mostrar una notificaci√≥n de error aqu√≠
      }
    };

    return tasks.map((task) => {
      // Log para cada tarea que se va a renderizar
      console.log("Renderizando tarea:", task.meta, task._id);

      return (
        <div className={style.taskContainer}>
          <div key={task._id}   className={`${style.taskCard} ${task.completada ? style.completed : ''} ${style[task.color] || style.color1}`}>


            <div className={style.taskHeader}>

              <p className={style.taskMeta}>{task.meta}</p>
              <p className={style.taskUrgency}>Urgencia &gt; {task.urgencia}</p>
            </div>
            <div className={style.taskDetails}>

              <p>Horario: {task.horario || 'Sin horario'}</p>
            </div>
            {/* 4. A√ëADIMOS EL CHECKBOX */}
            <div className={style.taskActions}>
              {/* ‚úÖ SOLUCI√ìN 2: A√±adido el texto de estado din√°mico */}
              <p className={task.completada ? style.statusDone : style.statusPending}>
                {task.completada ? 'Hecho' : 'Pendiente'}
              </p>

              <input
                type="checkbox"
                checked={task.completada}
                onChange={() => handleToggleComplete(task._id, task.completada)}
              />
                <button
                onClick={() => handleDeleteTask(task._id)}
                className={style.deleteButton} 
              >
                üóëÔ∏è
              </button>
            </div>
              {/* --- 3. A√ëADE EL BOT√ìN DE BORRAR AQU√ç --- */}

          </div>
        </div>
      );
    });
  };


  return (
    <div className={style.container}>

      <div className={style.header}>
        <h1>Mis Tareas</h1>
          <DatePicker   type="date"
          value={selectedDate}
          onChange={handleDateChange}
          className={style.datePicker} />

      </div>

      <div className={style.tasksList}>
        {renderContent()}
      </div>
    </div>
  );
}

export default Tareas;
