// Results.js (Versión Final Corregida)

import { useEffect, useState } from 'react';
import axios from 'axios';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import style from '../style/Results.module.css';

function Results({ onLoginClick, activeView, onCloseModal, token, onAuthSuccess }) {
  const [movimientos, setMovimientos] = useState([]);

  useEffect(() => {
    const fetchMovimientos = async () => {
      if (!token) {
        setMovimientos([]);
        return;
      }
      try {
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        setMovimientos([]);
      }
    };
    fetchMovimientos();
  }, [token]);

  const formatCurrency = (number) => { /* ... tu función ... */ };

  // ====================================================================
  // AQUÍ ESTÁ EL CAMBIO CLAVE:
  // Si NO hay token, mostramos los formularios de autenticación.
  // ====================================================================
  if (!token) {
    return (
      <div>
        {/* Muestra Registro por defecto, o el pop-up de Login si está activo */}
        {!activeView && (
          <SignupPage 
            onSwitchToLogin={onLoginClick} 
            onAuthSuccess={onAuthSuccess} 
          />
        )}

        {activeView === 'login' && (
          <LoginPage 
            onClose={onCloseModal} 
            onAuthSuccess={onAuthSuccess} 
          />
        )}
      </div>
    );
  }

  // ====================================================================
  // Si SÍ hay un token, el usuario está autenticado. Mostramos sus movimientos.
  // ====================================================================
  return (
    <div className={style.container}>
      <div>
        <h2>Movimientos</h2>
        <div className={style.containerInfoAll}>
          {/* Si el usuario no tiene movimientos, mostramos un mensaje amigable */}
          {movimientos.length === 0 && (
            <p style={{ textAlign: 'center', marginTop: '2rem' }}>
              Aún no tienes movimientos. ¡Añade uno para comenzar!
            </p>
          )}

          {/* El .map solo renderizará si hay movimientos */}
          {movimientos.map((mov) => (
            <div className={`${style.containerInfo} ${mov.tipo === 'ingreso' ? style.bordeIngreso : style.bordeEgreso}`} key={mov._id}>
              <div className={style.info}>
                <p className={style.category}>{mov.categoria}</p>
                <p className={style.detalle}>{mov.detalle}</p>
              </div>
              <p className={style.monto}>{formatCurrency(mov.monto)}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Results;
