// Add.jsx (CORREGIDO)

import { useState } from "react";
import axios from "axios";
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import style from '../style/Add.module.css';
import InputMonto from "./InputMonto"; 

function Add({ onMovementAdded }) {
  const [ingresoMonto, setIngresoMonto] = useState('');
  const [egresoMonto, setEgresoMonto] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // --- 游녢 SOLUCI칍N: CREAMOS LA VARIABLE CON LA FECHA DE HOY AQU칈 游녢 ---
  const fechaDeHoy = new Date().toISOString().slice(0, 10);

  const handleSubmit = async (e, tipo, monto) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const token = localStorage.getItem("token");

    if (!token) {
      setLoading(false);
      return setError("No se encontr칩 autorizaci칩n. Inicia sesi칩n de nuevo.");
    }

    const form = e.target;
    const montoNumerico = parseFloat(monto);

    if (isNaN(montoNumerico) || montoNumerico <= 0) {
      setLoading(false);
      return setError("Por favor, ingresa un monto v치lido.");
    }

    const data = {
      tipo,
      monto: montoNumerico,
      categoria: form.categoria.value,
      // Usamos la fecha del formulario. Si est치 vac칤a, el backend usar치 la fecha actual.
      fecha: form.fecha.value || fechaDeHoy,
      detalle: form.detalle.value
    };

    try {
      await axios.post("http://localhost:3000/api/add", data, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      form.reset();
      if (tipo === "ingreso") setIngresoMonto('');
      else setEgresoMonto('');

      if (onMovementAdded) {
        onMovementAdded();
      }

    } catch (err) {
      setError(err.response?.data?.error || "Error al guardar el movimiento");
    } finally {
      setLoading(false); 
    }
  };

  return (
    <div className={style.container}>
      {loading && <div className={style.spinner}></div>}

      <p className={style.title}>A침adir ingreso / A침adir egreso</p>
      <p className={style.subTitle}>Fecha</p>
      <div className={style.containerAllForm}>
        <form className={style.formone} onSubmit={(e) => handleSubmit(e, "ingreso", ingresoMonto)}>
            <InputMonto className={style.input} value={ingresoMonto} onChange={setIngresoMonto} />
            <div className={style.containerBtn}>
                <input name="categoria" className={style.btn} placeholder="Categor칤a" required />
                {/* Usamos la variable que creamos para el valor por defecto */}
                <input name="fecha" className={style.date} type="date" defaultValue={fechaDeHoy} />
            </div>
            <div className={style.containerDetalle}>
                <input name="detalle" className={style.detalle} placeholder="Detalle" />
                <button className={style.buttonSend} type="submit" disabled={loading}>A침adir ingreso</button>
            </div>
        </form>
        <form className={style.formtwo} onSubmit={(e) => handleSubmit(e, "egreso", egresoMonto)}>
            <InputMonto className={style.input} value={egresoMonto} onChange={setEgresoMonto} />
            <div className={style.containerBtn}>
                <input name="categoria" className={style.btn} placeholder="Categor칤a" required />
                {/* Usamos la variable que creamos para el valor por defecto */}
                <input name="fecha" className={style.date} type="date" defaultValue={fechaDeHoy} />
            </div>
            <div className={style.containerDetalle}>
                <input name="detalle" className={style.detalle} placeholder="Detalle" />
                <button className={style.buttonSend} type="submit" disabled={loading}>A침adir egreso</button>
            </div>
        </form>
      </div>
      {error && <p style={{ color: 'red', textAlign: 'center', marginTop: '1rem' }}>{error}</p>}
    </div>
  );
}

export default Add;
