// src/components/MainLayout.jsx

import React from 'react';
// 1. Importa useLocation de react-router-dom
import { Outlet, useLocation } from 'react-router-dom';
import Nav from './Nav';
import LeftsSite from './LeftsSite';
import style from '../style/App.module.css';
import LeftSideNotas from './LeftSideNotas';

function MainLayout({ token, onLogout, movimientos }) {
  // 3. Obtenemos la información de la ubicación actual
  const location = useLocation();

  return (
    <div className={style.container}>
      <Nav token={token} onLogout={onLogout} />
      <div className={style.content}>

        {/* --- LÓGICA PRINCIPAL --- */}
        {/* 4. Decidimos qué sidebar renderizar basándonos en la URL */}
        {location.pathname === '/notas' ? (
          <LeftSideNotas />
        ) : (
          <LeftsSite movimientos={movimientos} />
        )}

        {/* El Outlet sigue renderizando el contenido de la derecha */}
        <Outlet />
      </div>
    </div>
  );
}

export default MainLayout;
