import { useState, useMemo } from 'react';
import axios from 'axios';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import style from '../style/Results.module.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

function Results({ token, onAuthSuccess, onLoginClick, onCloseModal, activeView, onEditClick, movimientos, onMovementUpdate, onShowAllChange }) {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showAll, setShowAll] = useState(false);

  // --- FILTRADO DE MOVIMIENTOS ---
  const filteredMovimientos = useMemo(() => {
    if (!Array.isArray(movimientos)) return [];

    if (showAll) return movimientos;

    const localSelectedDate = new Date(selectedDate);
    localSelectedDate.setMinutes(localSelectedDate.getMinutes() - localSelectedDate.getTimezoneOffset());
    const formattedSelectedDate = localSelectedDate.toISOString().slice(0, 10);

    return movimientos.filter(mov => mov.fecha.slice(0, 10) === formattedSelectedDate);
  }, [movimientos, selectedDate, showAll]);

  // --- MANEJADORES ---
  const handleDateChange = (date) => {
    setSelectedDate(date);
    setShowAll(false); // volver a filtrar por fecha
    if (onShowAllChange) onShowAllChange(false);
  };


  const handleAllMovimientos = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/add/all`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.data) {
        if (onMovementUpdate) onMovementUpdate(res.data);
        setShowAll(true);
        if (onShowAllChange) onShowAllChange(true);
      }
    } catch (err) {
      console.error("Error al obtener todos los movimientos:", err);
      alert("No se pudieron obtener los movimientos.");
    }
  };

  const handleDeleteMovimiento = async (movimientoId) => {
    if (!window.confirm("¿Estás seguro de que quieres eliminar este movimiento?")) return;
    try {
      await axios.delete(`${API_URL}/api/add/${movimientoId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (onMovementUpdate) onMovementUpdate();
    } catch (err) {
      console.error("Error al eliminar el movimiento:", err);
      alert("No se pudo eliminar el movimiento.");
    }
  };



  // --- RENDER LOGIN / REGISTRO ---
  if (!token) {
    return (
      <div>
        {activeView === 'login' ? (
          <LoginPage onClose={onCloseModal} onAuthSuccess={onAuthSuccess} />
        ) : (
          <SignupPage onSwitchToLogin={onLoginClick} onAuthSuccess={onAuthSuccess} />
        )}
      </div>
    );
  }

  // --- RENDER PRINCIPAL ---
  return (
    <div className={style.container}>
      <div className={style.header}>
        <h2>{showAll ? 'Todos los Movimientos' : 'Movimientos del día'}</h2>
        <div className={style.datePickerContainer}>
          {!showAll && (
            <DatePicker
              selected={selectedDate}
              onChange={handleDateChange}
              dateFormat="dd-MM-yyyy"
              className={style.datePicker}
            />
          )}

          {!showAll ? (
            <button onClick={handleAllMovimientos} className={`${style.icon} `}>
              <img src="/list.png" alt="lista" />

            </button>
          ) : (
            <button
              onClick={() => {
                setShowAll(false);
                if (onShowAllChange) onShowAllChange(false);
              }}
              className={style.icon}
            >
              <img src="/cuadrado.png" alt="cuadrado" />
            </button>
          )}
        </div>

      </div>

    {showAll && (
  <div className={style.allMovimientosContainer}>
    <table className={style.movimientosTable}>
      <thead className={style.titleMovimientos}>
        <tr>
          <th>Fecha</th>
          <th>Tipo</th>
          <th>Categoría</th>
          <th>Detalle</th>
          <th>Monto</th>
          <th>Acciones</th>
        </tr>
      </thead>

      <tbody>
        {movimientos.map((mov) => (
          <tr key={mov._id}>
            <td>{new Date(mov.fecha).toLocaleDateString()}</td>
            <td>{mov.tipo}</td>
            <td>{mov.categoria}</td>
            <td>{mov.detalle}</td>
            <td>
              {mov.tipo === 'ingreso' ? '+' : '-'} ${mov.monto}
            </td>
            <td>
              <button onClick={() => onEditClick(mov)}>Editar</button>
              <button onClick={() => handleDeleteMovimiento(mov._id)}>
                Borrar
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
)}

    </div>
  );
}

export default Results;
