import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:3000";

const api = axios.create({
  baseURL: API_URL,
});

// INTERCEPTOR DE PETICIÓN: Inserta el token automáticamente si existe
api.interceptors.request.use((config) => {
  const token = localStorage.getItem("token") || sessionStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// INTERCEPTOR DE RESPUESTA: Maneja errores globales (como el 401)
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response && error.response.status === 401) {
      localStorage.removeItem("token");
      sessionStorage.removeItem("token");
      window.location.href = "/";
    }
    return Promise.reject(error);
  }
);

// Lógica de Tareas
export const taskService = {
  getAll: () => api.get("/api/task"),
  getByDate: (fecha) => api.get(`/api/task?fecha=${fecha}`),
  updateStatus: (taskId, data) => api.put(`/api/task/${taskId}/status`, data),
  delete: (taskId) => api.delete(`/api/task/${taskId}`),
};

// Lógica de Movimientos (Add / Leftside)
export const movimientoService = {
  getAll: () => api.get("/api/add"),
  create: (data) => api.post("/api/add", data),
  update: (id, data) => api.put(`/api/add/${id}`, data),
};

export default api;
