import { useState } from "react";
import axios from "axios";
import style from '../style/Add.module.css';

function Add() {
   const [valorVisible, setValorVisible] = useState('');
  const [fecha] = useState(new Date().toISOString().split("T")[0]); // formato YYYY-MM-DD
const handleChange = (e) => {
    let input = e.target.value;

    // 1. Eliminar todo excepto números y coma decimal
    let valorLimpio = input.replace(/[^\d,]/g, '');

    // 2. Permitir solo una coma para decimales
    valorLimpio = valorLimpio.replace(/,+/g, match => match.length > 1 ? ',' : match);

    // 3. Limitar a 2 decimales después de la coma
    const partes = valorLimpio.split(',');
    if (partes[1] && partes[1].length > 2) {
      valorLimpio = `${partes[0]},${partes[1].substring(0, 2)}`;
    }

    // 4. Formatear parte entera con puntos
    if (partes[0]) {
      const parteEntera = partes[0].replace(/\B(?=(\d{3})+(?!\d))/g, '.');
      valorLimpio = partes[1] ? `${parteEntera},${partes[1]}` : parteEntera;
    }

    setValorVisible(valorLimpio);
  };

  // Valor numérico real (para enviar al backend)
  const valorNumerico = parseFloat(valorVisible.replace(/\./g, '').replace(',', '.')) || 0;
  const handleSubmit = async (e, tipo) => {
    e.preventDefault();
    const form = e.target;

    const data = {
      tipo, // "ingreso" o "egreso"
      monto: form.monto.value,
      categoria: form.categoria.value,
      fecha: form.fecha.value,
      detalle: form.detalle.value
    };

    try {
      const res = await axios.post("http://localhost:3000/api/add", data);

      if (res.status === 201) {
        alert(`${tipo} añadido con éxito`);
        form.reset();
      }
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Error al guardar el movimiento");
    }
  };

  return (
    <div className={style.container}>
      <p className={style.title}>Añadir ingreso / Añadir egreso</p>
      <p className={style.subTitle}>Fecha</p>
      <div className={style.containerAllForm}>

        {/* Formulario INGRESO */}
        <form
          className={style.formone}
          onSubmit={(e) => handleSubmit(e, "ingreso")}
        >
          <input name="monto" className={style.input} type="number"  value={valorVisible}
        onChange={handleChange} placeholder="Ingresar monto" required />
          <div className={style.containerBtn}>
            <input name="categoria" className={style.btn} placeholder="Categoría" required />
            <input name="fecha" className={style.date} type="date" defaultValue={fecha} />
          </div>
          <div className={style.containerDetalle}>
            <input name="detalle" className={style.detalle} placeholder="Detalle" />
            <button className={style.buttonSend} type="submit">Añadir ingreso</button>
          </div>
        </form>

        {/* Formulario EGRESO */}
        <form
          className={style.formtwo}
          onSubmit={(e) => handleSubmit(e, "egreso")}
        >
          <input name="monto" className={style.input} type="number" placeholder="Ingresar monto" required />
          <div className={style.containerBtn}>
            <input name="categoria" className={style.btn} placeholder="Categoría" required />
            <input name="fecha" className={style.date} type="date" defaultValue={fecha} />
          </div>
          <div className={style.containerDetalle}>
            <input name="detalle" className={style.detalle} placeholder="Detalle" />
            <button className={style.buttonSend} type="submit">Añadir egreso</button>
          </div>
        </form>

      </div>
    </div>
  );
}

export default Add;
