import { useState, useEffect, forwardRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import style from "../style/Add.module.css";
import InputMonto from "./InputMonto";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:3000";

function Add({ onMovementAdded, movementToEdit, only }) {
  const navigate = useNavigate();

  // 游댳 EDIT MODE
  const isEditing = Boolean(movementToEdit);

  // 游댳 STATES
  const [ingresoMonto, setIngresoMonto] = useState("");
  const [egresoMonto, setEgresoMonto] = useState("");
  const [categoriaIngreso, setCategoriaIngreso] = useState("");
  const [categoriaEgreso, setCategoriaEgreso] = useState("");
  const [detalleIngreso, setDetalleIngreso] = useState("");
  const [detalleEgreso, setDetalleEgreso] = useState("");
  const [selectedDateIngreso, setSelectedDateIngreso] = useState(new Date());
  const [selectedDateEgreso, setSelectedDateEgreso] = useState(new Date());

  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // 游댳 MOSTRAR FORMULARIOS
  const mostrarIngreso =
    (!isEditing && (!only || only === "ingreso")) ||
    (isEditing && movementToEdit.tipo === "ingreso");

  const mostrarEgreso =
    (!isEditing && (!only || only === "egreso")) ||
    (isEditing && movementToEdit.tipo === "egreso");

  // 游댳 CARGA DE DATOS AL EDITAR
  useEffect(() => {
    if (isEditing) {
      const fecha = new Date(movementToEdit.fecha);
      fecha.setMinutes(fecha.getMinutes() + fecha.getTimezoneOffset());

      if (movementToEdit.tipo === "ingreso") {
        setIngresoMonto(movementToEdit.monto.toString());
        setCategoriaIngreso(movementToEdit.categoria);
        setDetalleIngreso(movementToEdit.detalle || "");
        setSelectedDateIngreso(fecha);

        setEgresoMonto("");
        setCategoriaEgreso("");
        setDetalleEgreso("");
      } else {
        setEgresoMonto(movementToEdit.monto.toString());
        setCategoriaEgreso(movementToEdit.categoria);
        setDetalleEgreso(movementToEdit.detalle || "");
        setSelectedDateEgreso(fecha);

        setIngresoMonto("");
        setCategoriaIngreso("");
        setDetalleIngreso("");
      }
    }
  }, [isEditing, movementToEdit]);

  // 游댳 DATE PICKER
  const CustomDateInput = forwardRef(({ value, onClick }, ref) => (
    <input
      className={style.datePicker}
      onClick={onClick}
      ref={ref}
      value={value}
      readOnly
    />
  ));
  CustomDateInput.displayName = "CustomDateInput";

  // 游댳 SUBMIT
  const handleSubmit = async (e, tipo) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    const isIngreso = tipo === "ingreso";

    const monto = isIngreso ? ingresoMonto : egresoMonto;
    const categoria = isIngreso ? categoriaIngreso : categoriaEgreso;
    const detalle = isIngreso ? detalleIngreso : detalleEgreso;
    const fecha = isIngreso ? selectedDateIngreso : selectedDateEgreso;

    if (!monto || Number(monto) <= 0) {
      setError("Monto inv치lido");
      setLoading(false);
      return;
    }

    const fechaLocal = new Date(fecha);
    fechaLocal.setMinutes(
      fechaLocal.getMinutes() - fechaLocal.getTimezoneOffset()
    );

    const data = {
      tipo: isEditing ? movementToEdit.tipo : tipo,
      monto: Number(monto),
      categoria,
      detalle,
      fecha: fechaLocal.toISOString().slice(0, 10),
    };

    try {
      const token = localStorage.getItem("token");

      if (isEditing) {
        await axios.put(
          `${API_URL}/api/add/${movementToEdit._id}`,
          data,
          { headers: { Authorization: `Bearer ${token}` } }
        );
      } else {
        await axios.post(`${API_URL}/api/add`, data, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }

      onMovementAdded?.();
    } catch {
      setError("Error al guardar movimiento");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={style.container}>
      <p className={style.title}>
        {isEditing ? "Editando Movimiento" : "A침adir Movimiento"}
      </p>

      <div className={style.containerAllForm}>
        {/* INGRESO */}
        {mostrarIngreso && (
          <form
            className={style.formone}
            onSubmit={(e) => handleSubmit(e, "ingreso")}
          >
            <InputMonto value={ingresoMonto} onChange={setIngresoMonto} />

            <input
              className={style.btn}
              placeholder="Categor칤a"
              value={categoriaIngreso}
              onChange={(e) => setCategoriaIngreso(e.target.value)}
              required
            />

            <DatePicker
              selected={selectedDateIngreso}
              onChange={setSelectedDateIngreso}
              customInput={<CustomDateInput />}
            />

            <input
              className={style.detalle}
              placeholder="Detalle"
              value={detalleIngreso}
              onChange={(e) => setDetalleIngreso(e.target.value)}
            />

            <button className={style.buttonSend} type="submit">
              {isEditing ? "Guardar Cambios" : "A침adir ingreso"}
            </button>
          </form>
        )}

        {/* EGRESO */}
        {mostrarEgreso && (
          <form
            className={style.formtwo}
            onSubmit={(e) => handleSubmit(e, "egreso")}
          >
            <InputMonto value={egresoMonto} onChange={setEgresoMonto} />

            <input
              className={style.btn}
              placeholder="Categor칤a"
              value={categoriaEgreso}
              onChange={(e) => setCategoriaEgreso(e.target.value)}
              required
            />

            <DatePicker
              selected={selectedDateEgreso}
              onChange={setSelectedDateEgreso}
              customInput={<CustomDateInput />}
            />

            <input
              className={style.detalle}
              placeholder="Detalle"
              value={detalleEgreso}
              onChange={(e) => setDetalleEgreso(e.target.value)}
            />

            <button className={style.buttonSend} type="submit">
              {isEditing ? "Guardar Cambios" : "A침adir egreso"}
            </button>
          </form>
        )}
      </div>

      {isEditing && (
        <button
          className={style.cancelButton}
          onClick={() => navigate("/")}
        >
          Cancelar edici칩n
        </button>
      )}

      {error && <p className={style.errorText}>{error}</p>}
    </div>
  );
}

export default Add;
