// src/AppRoutes.jsx (VERSIÓN FINAL)

import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import MainLayout from './MainLayout';
import Dashboard from './Dashboard';
import Tareas from './Tareas';

function AppRoutes({ 
  token, 
  onLogout, 
  movimientos, 
  taskToEdit, 
  setTaskToEdit, 
  onTaskUpdate, 
  onMovementAdded,
  refreshKey, 
  ...authProps 
}) {
  return (
    <Routes>
      <Route
        path="/"
        element={
          <MainLayout 
            token={token} 
            onLogout={onLogout} 
            movimientos={movimientos}
            taskToEdit={taskToEdit}
            onTaskAdded={onTaskUpdate} // Pasamos la función de tareas al layout
          />
        }
      >
        <Route
          index
          element={
            <Dashboard
              token={token}
              refreshKey={refreshKey}
              onMovementAdded={onMovementAdded} // Pasamos la función de movimientos al dashboard
              {...authProps}
            />
          }
        />
        <Route
          path="notas"
          element={
            token ? (
              <Tareas 
                token={token} 
                refreshKey={refreshKey} 
                onEditClick={setTaskToEdit} 
              />
            ) : (
              <Navigate to="/" />
            )
          }
        />
      </Route>
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default AppRoutes;
